const raw = $input.first().json.output;
let clean = raw.trim();

if (clean.startsWith('```markdown')) {
  clean = clean.slice('```markdown'.length).trim();
}
if (clean.startsWith('```')) {
  clean = clean.slice(3).trim();
}
if (clean.endsWith('```')) {
  clean = clean.slice(0, -3).trim();
}

return [{ json: { llm_raw_text_clean: clean } }];

