return items.map(item => {
  // Get data from the Webhook node (since that's where your original data comes from)
  const webhookData = $('Webhook').first().json.body;

  console.log('Webhook data:', JSON.stringify(webhookData, null, 2));

  const treatmentType = webhookData.treatment_type;
  const firstName = webhookData.first_name;
  const fullName = webhookData.name;
  const jobTitle = webhookData.job_title;
  const company = webhookData.company;

  console.log('Extracted values:');
  console.log('Treatment Type:', treatmentType);
  console.log('First Name:', firstName);
  console.log('Full Name:', fullName);
  console.log('Job Title:', jobTitle);
  console.log('Company:', company);

  // Create filename based on treatment type
  let filename = '';
  if (treatmentType === 'control') {
    filename = `${firstName}_Resume_${company}_${jobTitle}`;
  } else if (treatmentType === 'Type_I') {
    filename = `Resume_${fullName}_${jobTitle}_${company}`;
  } else if (treatmentType === 'Type_II') {
    filename = `${firstName}_${jobTitle}_${company}_Resume`;
  } else if (treatmentType === 'Type_III') {
    filename = `${fullName}_Resume_${company}_${jobTitle}`;
  } else {
    filename = `${firstName}_Resume_${company}_${jobTitle}`;
  }

  // Sanitize the filename
  filename = filename
    .replace(/[:<>"|?*]/g, '')
    .replace(/[()]/g, '')
    .replace(/\s+/g, '_')
    .replace(/_{2,}/g, '_')
    .trim();

  item.json.sanitized_filename = filename;

  return item;
});

